        {
            talk_id=46061,
            talker=46599,
            talk_text="少侠，请留步，前方正在抵御兽潮侵袭，无关人员还请离开。",
            audio="302_01",
            audio_day=0
        },
        {
            talk_id=46061,
            talk_seq=2,
